const integrateAutomergeSmol = <T>(doc: Doc<T>, newItem: Item<T>) => {
  const {id} = newItem
  doc.version[id[0]] = id[1]

  let parent = findItem(doc, newItem.originLeft)

  let i

  // Scan to find the insert location
  for (i = parent + 1; i < doc.content.length; i++) {
    let o = doc.content[i]
    let oparent = findItem(doc, o.originLeft)

    // Should we insert here?
    if (oparent < parent
      || (oparent === parent
        && (newItem.seq === o.seq)
        && id[0] < o.id[0])
    ) break
  }

  // We've found the position. Insert at position *i*.
  doc.content.splice(i, 0, newItem)
  doc.maxSeq = Math.max(doc.maxSeq, newItem.seq)
}
